import React from "react";
import "./Home.css";
import { Col, Card, Row, Button } from "react-bootstrap";
import Movies from "./Movies";
import { IoMdStar } from "react-icons/io";

const moviesData = [
  {
    title: "Interstellar",
    genre: "Scifi-Action",
    rating: "8.7/10",
    backgroundImage: "./images/intersteller.jpg",
  },
  {
    title: "Beautiful Mind",
    genre: "Action-Drama",
    rating: "PG",
    backgroundImage: "./images/beautifulmind.jpeg",
  },
  {
    title: "Shawshank Redemption",
    genre: "Drama",
    rating: "R",
    backgroundImage: "./images/shawshank.jpeg",
  },
  {
    title: "Into the Wild",
    genre: "Scifi-Action",
    rating: "PG-13",
    backgroundImage: "./images/intothewild.jpeg",
  },
];

const Home = () => {
  return (
    <>
      <div class="section">
        <h1 class="title">Recommended Movies</h1>
      </div>

      <div>
        {moviesData.map((movie, index) => (
          <MovieCard key={index} movie={movie} />
        ))}
      </div>

      <Row xs={1} md={2} lg={3} xl={4}>
        <Col>
          <Card style={{ width: "18rem", margin: "1rem" }}>
            <Card.Img
              variant="top"
              src={moviesData[0].backgroundImage}
              alt={moviesData[0].title}
              style={{ height: "380px" }}
            />
            <Card.Body>
              <Card.Title>{moviesData[0].title}</Card.Title>
              <Card.Text>Genre: {moviesData[0].genre}</Card.Text>{" "}
              <Row>
                <Col>
                  <Button variant="danger">Book</Button>
                </Col>
                <Col>
                  <p>
                    <IoMdStar style={{ fontSize: "30px" }} /> 
                    {moviesData[0].rating}/10
                  </p>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Movies />
    </>
  );
};

const MovieCard = ({ movie }) => {
  return (
    <Col>
      <Card style={{ width: "18rem", margin: "1rem" }}>
        <Card.Img
          variant="top"
          src={movie.backgroundImage}
          alt={movie.title}
          style={{ height: "380px" }}
        />
        <Card.Body>
          <Card.Title>{movie.title}</Card.Title>
          <Card.Text>Genre: {movie.genre}</Card.Text>{" "}
          <Row>
            <Col>
              <Button variant="danger">Book</Button>
            </Col>
            <Col>
              <p>
                <IoMdStar style={{ fontSize: "30px" }} /> {movie.rating}/10
              </p>
            </Col>
          </Row>
        </Card.Body>
      </Card>
    </Col>
  );
};

export default Home;
